var app={initialize:function(){this.bindEvents()},bindEvents:function(){document.addEventListener("deviceready",this.onDeviceReady,!1)},onDeviceReady:function(){navigator.geolocation.getCurrentPosition(app.onSuccess,app.onError)},onSuccess:function(a){var b=a.coords.longitude,c=a.coords.latitude,d=new google.maps.LatLng(c,b),e={center:d,zoom:13,mapTypeId:google.maps.MapTypeId.ROADMAP},f=new google.maps.Map(document.getElementById("map"),e);new google.maps.Marker({position:d,map:f,title:"my location"})},onError:function(a){alert("the code is "+a.code+". \nmessage: "+a.message)}};app.initialize();